const timerBlocks = document.querySelectorAll(".clock__item-block");

function dataTimer() {
  let dateEnd = new Date("02.22.2026").getTime();
  let dateStart = new Date().getTime();
  let time = dateEnd - dateStart;

  if (time > 0) {
    let days = Math.floor(time / (1000 * 60 * 60 * 24));
    let hours = Math.floor((time % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    let minutes = Math.floor((time % (1000 * 60 * 60)) / (1000 * 60));
    let seconds = Math.floor((time % (1000 * 60)) / 1000);

    let timing = [days, hours, minutes, seconds];

    timerBlocks.forEach((block, idx) => {
      let oldSpan = block.querySelector(".current");
      if (oldSpan && oldSpan.textContent == timing[idx].toString()) return;

      let newSpan = document.createElement("span");
      newSpan.textContent = timing[idx];
      newSpan.classList.add("new");
      block.appendChild(newSpan);

      // Ֆլիպ անիմացիա
      setTimeout(() => {
        oldSpan.style.transform = "translateY(-100%)";
        oldSpan.style.opacity = "0";
        newSpan.style.transform = "translateY(0)";
        newSpan.style.opacity = "1";
      }, 50);

      // Հին թիվը հեռացնում ենք
      setTimeout(() => {
        oldSpan?.remove();
        newSpan.classList.remove("new");
        newSpan.classList.add("current");
      }, 500);
    });
  }
}

// Սկզբում "current" դասը ավելացնում ենք բոլոր span-ներին
document.querySelectorAll(".clock__item span").forEach((span) => {
  span.classList.add("current");
});

setInterval(dataTimer, 1000);
